import React, { useState, useEffect } from 'react'
import {
  Card,
  CardHeader,
  CardMedia,
  IconButton,
  ListItemIcon,
  Menu,
  MenuItem,
  Tooltip,
  Typography,
  makeStyles,
} from '@material-ui/core'
import { ipcRenderer } from 'electron'

import PlaylistAddIcon from '@material-ui/icons/PlaylistAdd'
import DeleteIcon from '@material-ui/icons/Delete'
import MoreVertIcon from '@material-ui/icons/MoreVert'
import TextFieldsIcon from '@material-ui/icons/TextFields'

const useStyles = makeStyles({
  card: {
    width: 250,
    cursor: 'pointer',
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
})

interface RecentSessionData {
  path: string
  name: string
  updated: number
  screenshotPath?: string
}

function RecentSessionCard({
  sessionData,
  onClick,
  onDelete,
  onRename,
  onAddToQuickstartList,
}: {
  sessionData: RecentSessionData
  onClick: (arg: RecentSessionData) => void
  onDelete: (arg: RecentSessionData) => void
  onRename: (arg: RecentSessionData) => void
  onAddToQuickstartList: (arg: RecentSessionData) => void
}) {
  const classes = useStyles()
  const [hovered, setHovered] = useState(false)
  const [menuAnchorEl, setMenuAnchorEl] = useState<HTMLElement | null>(null)
  const [screenshot, setScreenshot] = useState<string>()
  const { name, path } = sessionData

  useEffect(() => {
    ;(async () => {
      try {
        const data = await ipcRenderer.invoke('loadThumbnail', path)
        if (data) {
          setScreenshot(data)
        } else {
          setScreenshot(defaultSessionScreenshot)
        }
      } catch (e) {
        console.error(e)
        setScreenshot(defaultSessionScreenshot)
      }
    })()
  }, [path])

  return (
    <>
      <Card
        className={classes.card}
        onMouseOver={() => setHovered(true)}
        onMouseOut={() => setHovered(false)}
        onClick={() => onClick(sessionData)}
        raised={Boolean(hovered)}
      >
        {screenshot ? (
          <CardMedia
            className={classes.media}
            image={screenshot || defaultSessionScreenshot}
          />
        ) : null}
        <CardHeader
          action={
            <IconButton
              onClick={event => {
                event.stopPropagation()
                setMenuAnchorEl(event.currentTarget)
              }}
            >
              <MoreVertIcon color="secondary" />
            </IconButton>
          }
          disableTypography
          title={
            <Tooltip title={name} enterDelay={300}>
              <Typography variant="body2" noWrap style={{ width: 178 }}>
                {name}
              </Typography>
            </Tooltip>
          }
          subheader={
            <Typography
              variant="body2"
              color="textSecondary"
              noWrap
              style={{ width: 178 }}
            >
              Last modified{' '}
              {sessionData
                ? `${new Date(sessionData.updated).toLocaleString('en-US')}`
                : null}
            </Typography>
          }
        />
      </Card>
      <Menu
        anchorEl={menuAnchorEl}
        keepMounted
        open={Boolean(menuAnchorEl)}
        onClose={() => setMenuAnchorEl(null)}
      >
        <MenuItem
          onClick={() => {
            setMenuAnchorEl(null)
            onRename(sessionData)
          }}
        >
          <ListItemIcon>
            <TextFieldsIcon color="secondary" />
          </ListItemIcon>
          <Typography variant="inherit">Rename</Typography>
        </MenuItem>
        <MenuItem
          onClick={() => {
            onDelete(sessionData)
            setMenuAnchorEl(null)
          }}
        >
          <ListItemIcon>
            <DeleteIcon color="secondary" />
          </ListItemIcon>
          <Typography variant="inherit">Delete</Typography>
        </MenuItem>
        <MenuItem
          onClick={() => {
            onAddToQuickstartList(sessionData)
            setMenuAnchorEl(null)
          }}
        >
          <ListItemIcon>
            <PlaylistAddIcon color="secondary" />
          </ListItemIcon>
          <Typography variant="inherit">Add to quickstart list</Typography>
        </MenuItem>
      </Menu>
    </>
  )
}

const defaultSessionScreenshot =
  'data:image/png;base64,'

export default RecentSessionCard
